/** 2. Write a PHP program to check whether the given number is prime or not using for loop. **/

<html>
<body>
<form method="post">
Enter a number:
<input type="text" name="num">
<input type="submit" name="Submit">
</form>
</body>
</html>
<?php
if($_POST)
{
	$num=$_POST['num'];
	function prime($num)
	{
		for($x=2;$x<$num;$x++)
		{
			if($num%$x==0)
			{
				return 0;
			}
		}
		return 1;
	}
	$a=prime($num);
	if($a==0)
			echo "$num is not a prime number";
	else
			echo "$num is prime number";
}
?>

/** 3. Write a PHP program to reverse a given number using while loop. **/

<html>
<font face=Consolas size=30 color=red>
<?php
$num=156;
$revnum=0;
echo "The given number is $num <br />";
while($num>1)
{
	$rem=$num%10;
	$revnum=($revnum*10)+$rem;
	$num=($num/10);
}
echo "Reverse number is $revnum";
?>	
</font>
</html>

/** 4. Write a PHP program to print the Fibonacci series using functions. **/

<?php
echo ("<h2>Fibonacci Series</h2>");
function fib($n)
{
	$f1=0;
	$f2=1;
	print "Fibonacci series <br />";
	echo "$f1<br />$f2<br />";
	for($i=2;$i<$n;$i++)
	{
		$f3=$f1+$f2;
		echo "$f3<br />";
		$f1=$f2;
		$f2=$f3;
	}
}
print fib(5);
?>

/** 5. Write a PHP program to illustrate different string functions. **/

<?php
$str1="PHP is a SCRIPTING Language";
$str2=" Different String Functions ";
echo "The String1 is $str1";
echo "<br />The length of the string1 is ".strlen($str1);
echo "<br />The length of the string2 is ".strlen($str2);
echo "<br />After trimming string2 is ".strlen(trim($str2));
echo "<br />Number of words in the string1 is ".str_word_count($str1);
echo "<br />The reverse of the string1 is ".strrev($str1);
echo "<br />String1 is in Lower case ".strtolower($str1);
echo "<br />String1 is in Lower case ".strtoupper($str1);
echo "<br />After replace php to java is ".str_replace("PHP","Java",$str1);
echo "<br />Sub string of string1 is ".substr($str1,19,27);
echo "<br />The language is present at ".strpos($str1,'Language')." position";
?>

/** 6. Write a PHP program to implement calculator using switch case. **/

<html>
<body>
<form method="POST">
<h2>First Number:</h2><input type="text" name="num1">
<h2>Second Number:</h2><input type="text" name="num2">
<h2>Enter Operator</h2>
<select name="operator">
<option>+</option>
<option>-</option>
<option>*</option>
<option>/</option>
</select>
<input type="submit" name="sbt" value="Submit">
</form>
</body>
</html>
<?php
if(isset($_POST["sbt"]))
{
	$n1=$_POST['num1'];
	$n2=$_POST['num2'];
	$cal=$_POST['operator'];
	switch("$cal")
	{
		case "+"; 
		$result=$n1+$n2;
		echo "Addition of $n1 and $n2=$result";
		break;
		case "-"; 
		$result=$n1-$n2;
		echo "Subraction of $n1 and $n2=$result";
		break;
		case "*"; 
		$result=$n1*$n2;
		echo "Multiplication of $n1 and $n2=$result";
		break;
		case "/"; 
		$result=$n1/$n2;
		echo "Division of $n1 and $n2=$result";
		break;
	}
}	
?>

/** 7. Write a PHP program to find factorial of a number using for each loop. **/

<?php
$n=4;
$fact=1;
$a=range(1,$n);
foreach($a as $val)
{
	$fact=$fact*$val;
}
echo "The Factorial of $n is $fact";
?>

