Or
<?php
$a=array(3,7,4,6,5);
echo "The given array is <br />";
foreach($a as $val)
echo "$val <br />";
for($i=0;$i<count($a);$i++)
{
	for($j=0;$j<=count($a)-1;$j++)
	{
		if($a[$i]<$a[$j])
		{
			$temp=$a[$i];
			$a[$i]=$a[$j];
			$a[$j]=$temp;
		}
	}
}
echo "The sorted array is <br />";
for($i=0;$i<count($a);$i++)
	echo "$a[$i]<br />";
?>

