/** 10. Write a PHP program to create and validate a email id. **/

<?php
$email="sdctmk@gmail.com";
if(preg_match("/^[a-zA-Z0-9,_-]+@[a-zA-Z0-9_]+\.[a-zA-z.]{2,5}$/",$email))
{
	echo "$email is valid";
}
else
{
	echo "$email is not valid";
}
?>
Or
<?php
$email="sdctmk@gmail.com";
if(filter_var($email,FILTER_VALIDATE_EMAIL))
{
	echo "$email is valid";
}
else
{
	echo "$email is not valid";
}
?>

