//Creating a table
*****************************************************************************************************************
--------- Here "db_Employee" is the database and "Employee" is the table in the database "db_Employee"--------
*****************************************************************************************************************

CREATE TABLE `db_Employee`.`Employee` 
(
	`name` TEXT NOT NULL ,
	`empid` VARCHAR(20) NOT NULL , 
	`bsal` BIGINT(20) NOT NULL ,
	`da` BIGINT(20) NOT NULL ,
	`hra` BIGINT(20) NOT NULL , 
	`pf` BIGINT(20) NOT NULL ,
	`tax` BIGINT(20) NOT NULL , 
	`gross` BIGINT(20) NOT NULL ,
	`net` BIGINT(20) NOT NULL
 )
ENGINE = InnoDB;

